mapper.select:
select qq.* from
(select q.*,rownum rn from quiz q order by errTotal
desc) qq where 100>=rn , qid=#{qid}

mapper.update:
update quiz set 
	question = #{question} , answer = #{answer} ,
	pid = #{pid} , pic = #{pic} ,
	qtype = #{qtype} , errTotal = #{errTotal} ,
	explain = #{explain} 
where qid = #{qid}
