mapper.insert:
insert into TestRecord values 
( seq_testRecord_recid.nextval , #{cid} , #{testType} ,#{testKind} 
, sysdate , #{errorCount} ,#{qID},#{recTemp} )

mapper.update:
update TestRecord set 
	testType = #{testType} ,testKind = #{testKind} ,
	errorCount= #{errorCount} ,qID = #{qID} ,
	recTemp = #{recTemp}
where recId=#{recId}
